const url = `${process.env.AZURE_OPENAI_ENDPOINT || '{{endpoint}}'}/openai/deployments/${process.env.AZURE_OPENAI_EMBED_DEPLOYMENT || '{{deployment}}'}/embeddings?api-version=${process.env.AZURE_OPENAI_API_VERSION || '{{api_version}}'}`;
const res = await fetch(url, { method: 'POST', headers: { 'Content-Type': 'application/json', 'api-key': process.env.AZURE_OPENAI_API_KEY }, body: JSON.stringify({ input: ['hello world'] }) });
const json = await res.json();
console.log(json.data?.[0]?.embedding?.length);
