const url = `${process.env.AZURE_OPENAI_ENDPOINT || '{{endpoint}}'}/openai/deployments/${process.env.AZURE_OPENAI_DEPLOYMENT || '{{deployment}}'}/chat/completions?api-version=${process.env.AZURE_OPENAI_API_VERSION || '{{api_version}}'}`;
const body = { messages: [{ role: 'user', content: 'What time is it? Use the tool.' }], tools: [{ type: 'function', function: { name: 'get_time', description: 'Get current time', parameters: { type: 'object', properties: {} } } }] };
const res = await fetch(url, { method: 'POST', headers: { 'Content-Type': 'application/json', 'api-key': process.env.AZURE_OPENAI_API_KEY }, body: JSON.stringify(body) });
const json = await res.json();
console.log(JSON.stringify(json, null, 2));
