import sdk from 'microsoft-cognitiveservices-speech-sdk';
import fs from 'node:fs';
const speechConfig = sdk.SpeechConfig.fromSubscription(process.env.AZURE_SPEECH_KEY, process.env.AZURE_SPEECH_REGION || '{{speech_region}}');
const pushStream = sdk.AudioInputStream.createPushStream();
fs.createReadStream(process.env.AUDIO_FILE || '{{audio_file}}').on('data', s=>pushStream.write(s)).on('end', ()=>pushStream.close());
const audioConfig = sdk.AudioConfig.fromStreamInput(pushStream);
const recognizer = new sdk.SpeechRecognizer(speechConfig, audioConfig);
recognizer.recognizeOnceAsync(result => { console.log(result.text); recognizer.close(); });
