import { BlobSASPermissions, generateBlobSASQueryParameters, SASProtocol, StorageSharedKeyCredential } from '@azure/storage-blob';
const account = process.env.AZURE_ACCOUNT;
const key = process.env.AZURE_KEY;
const cred = new StorageSharedKeyCredential(account, key);
const sas = generateBlobSASQueryParameters({ containerName: process.env.AZURE_CONTAINER || '{{container}}', blobName: process.env.AZURE_BLOB || '{{blob}}', permissions: BlobSASPermissions.parse('r'), startsOn: new Date(), expiresOn: new Date(Date.now()+3600000), protocol: SASProtocol.Https }, cred).toString();
console.log(sas);
