import { BedrockRuntimeClient, InvokeModelCommand } from '@aws-sdk/client-bedrock-runtime';
const client = new BedrockRuntimeClient({ region: process.env.AWS_REGION || '{{aws_region}}' });
const body = { inputText: 'hello world' };
const cmd = new InvokeModelCommand({ modelId: process.env.TITAN_MODEL || '{{titan_model}}', body: JSON.stringify(body), contentType: 'application/json', accept: 'application/json' });
const resp = await client.send(cmd);
console.log(new TextDecoder().decode(resp.body));
