import { BedrockRuntimeClient, InvokeModelCommand } from '@aws-sdk/client-bedrock-runtime';
const client = new BedrockRuntimeClient({ region: process.env.AWS_REGION || '{{aws_region}}' });
const body = { taskType: 'TEXT_IMAGE', textToImageParams: { text: 'A cute robot waving' }, imageGenerationConfig: { numberOfImages: 1, quality: 'standard', width: 512, height: 512, cfgScale: 8 } };
const cmd = new InvokeModelCommand({ modelId: process.env.BEDROCK_TITAN_IMAGE || '{{model_id}}', body: JSON.stringify(body), contentType: 'application/json', accept: 'application/json' });
const resp = await client.send(cmd);
console.log('bytes=', (await resp.body?.transformToString?.())?.length || 0);
