const { Queue, Worker, QueueScheduler } = require('bullmq');
const queueName = 'jobs';
new QueueScheduler(queueName);
const q = new Queue(queueName);
new Worker(queueName, async (job) => { if (!job.attemptsMade) throw new Error('fail once'); return 'ok'; });
(async () => { await q.add('task', { t: Date.now() }, { delay: 1000, attempts: 2, backoff: { type: 'exponential', delay: 500 } }); console.log('enqueued'); })();
