const { Worker, Queue } = require('bullmq');
const connection = { url: process.env.REDIS_URL || 'redis://localhost:6379' };
new Worker(process.env.QUEUE || '{{queue}}', async job => ({ ok: true, id: job.id }), { connection });
new Queue(process.env.QUEUE || '{{queue}}', { connection }).add('hello', { a: 1 });
