const http = require('http');
const Busboy = require('busboy');
http.createServer((req,res)=>{ if(req.method==='POST'){ const bb=new Busboy({ headers:req.headers }); bb.on('file',(_n,_f,filename)=>console.log(filename)); req.pipe(bb); bb.on('finish',()=>res.end('ok')); } else { res.end('post'); } }).listen(3000);
