const { Client } = require('cassandra-driver');
const client = new Client({ contactPoints: ['127.0.0.1'], localDataCenter: 'datacenter1' });
(async()=>{ await client.connect(); await client.batch([{ query: 'INSERT INTO ks.table (id) VALUES (?)', params: [1] }], { prepare: true }); await client.shutdown(); })();
