const { Client } = require('cassandra-driver');
const client = new Client({ contactPoints: ['127.0.0.1'], localDataCenter: 'datacenter1' });
(async () => { try { await client.connect(); const rs = await client.execute('SELECT release_version FROM system.local'); console.log(rs.rows[0]); } finally { await client.shutdown(); } })();
