const { Client } = require('cassandra-driver');
const client = new Client({ contactPoints: (process.env.CASSANDRA_HOSTS||'127.0.0.1').split(','), localDataCenter: 'datacenter1', keyspace: process.env.KEYSPACE || '{{keyspace}}' });
(async () => {
  await client.connect();
  const res = await client.execute('SELECT now() FROM system.local');
  console.log(res.rows[0]);
  await client.shutdown();
})();
