const express = require('express');
const { celebrate, Joi, errors } = require('celebrate');
const app = express();
app.use(express.json());
app.post('/echo', celebrate({ body: Joi.object({ name: Joi.string().required() }) }), (req,res)=>res.json({ name: req.body.name }));
app.use(errors());
app.listen(Number('{{port}}'));
