import { ChromaClient } from 'chromadb';
const client = new ChromaClient();
const col = await client.getOrCreateCollection({ name: 'docs' });
await col.add({ ids: ['1','2'], documents: ['hello','fluorite'] });
await col.delete({ ids: ['1'] });
const res = await col.query({ queryTexts: ['hello'], nResults: 2 });
console.log(res);
