import { ChromaClient } from 'chromadb';
const client = new ChromaClient();
const col = await client.getOrCreateCollection({ name: 'docs' });
await col.add({ ids: ['1','2'], documents: ['hello','fluorite'], metadatas: [{ tag: 'a' }, { tag: 'b' }] });
await col.delete({ where: { tag: 'a' } });
const res = await col.query({ queryTexts: ['*'], nResults: 2 });
console.log(res);
