import { ChromaClient } from 'chromadb';
const client = new ChromaClient({ path: process.env.CHROMA_PATH || '{{persist_dir}}' });
const col = await client.getOrCreateCollection({ name: 'docs' });
await col.add({ ids: ['1'], documents: ['hello world'] });
const res = await col.query({ queryTexts: ['hello'], nResults: 1 });
console.log(res);
