import { ChromaClient } from 'chromadb';
const client = new ChromaClient();
const col = await client.getOrCreateCollection({ name: 'docs' });
await col.add({ ids: ['1'], documents: ['hello'], metadatas: [{ tag: 'a' }] });
await col.update({ ids: ['1'], metadatas: [{ tag: 'updated' }] });
const res = await col.get({ ids: ['1'] });
console.log(res?.metadatas);
