import { ChromaClient } from 'chromadb';
const client = new ChromaClient();
const col = await client.getOrCreateCollection({ name: 'docs' });
await col.add({ ids: ['1','2'], documents: ['hello world', 'fluorite mcp'] });
const res = await col.query({ queryTexts: ['hello'], nResults: 2, where_document: { $contains: 'hello' } });
console.log(res);
