import { ChromaClient } from 'chromadb';
const client = new ChromaClient();
const col = await client.getOrCreateCollection({ name: 'docs' });
await col.add({ ids: ['1','2','3'], documents: ['hello','fluorite','world'], metadatas: [{ tag: 'a' }, { tag: 'b' }, { tag: 'c' }] });
const res = await col.query({ queryTexts: ['hello'], nResults: 3, where: { $or: [{ tag: 'a' }, { tag: 'c' }] } });
console.log(res);
