const { createClient } = require('@clickhouse/client');
const ch = createClient({ host: '{{host}}' });
(async () => { await ch.query({ query: `CREATE TABLE IF NOT EXISTS logs (m String) ENGINE = Memory` }); await ch.insert({ table: 'logs', values: [{ m: 'hello' }] }); const rows = await ch.query({ query: `SELECT count() c FROM logs`, format: 'JSONEachRow' }).then(r=>r.json()); console.log(rows); })();
