import express from 'express';
import fetch from 'node-fetch';
const app = express();
app.use(express.json());
app.post('/verify', async (req,res)=>{ const token = req.body?.token; const r = await fetch('https://challenges.cloudflare.com/turnstile/v0/siteverify', { method: 'POST', headers: { 'content-type': 'application/json' }, body: JSON.stringify({ secret: process.env.TURNSTILE_SECRET||'', response: token }) }); const data = await r.json(); res.json(data); });
app.listen(Number('{{port}}'));
