import { CohereClient } from 'cohere-ai';
const cohere = new CohereClient({ token: process.env.COHERE_API_KEY });
const stream = await cohere.chatStream({ model: '{{cohere_model}}', message: 'Stream a short hello' });
for await (const event of stream) {
  if (event.eventType === 'text-generation') process.stdout.write(event.text || '');
}
console.log();
