import { CohereClient } from 'cohere-ai';
const cohere = new CohereClient({ token: process.env.COHERE_API_KEY });
const res = await cohere.classify({
  inputs: ['I love this', 'This is bad'],
  examples: [
    { text: 'I like it', label: 'positive' },
    { text: 'I hate it', label: 'negative' }
  ]
});
console.log(res.classifications?.map(c => c.prediction));
