import { CohereClient } from 'cohere-ai';
const cohere = new CohereClient({ token: process.env.COHERE_API_KEY });
const instructions = 'If asked for the time, respond with JSON {\

: \"get_time\"}';
const res = await cohere.chat({ model: 'command-r', message: 'What time is it?', preamble: instructions });
console.log(res.text);
