#!/usr/bin/env node
const { program } = require('commander');
program
  .name('{{app_name}}')
  .description('A minimal CLI powered by commander.js')
  .version('1.0.0');
program
  .command('hello')
  .description('Print hello')
  .option('-n, --name <name>', 'name', 'world')
  .action(opts => console.log(`hello ${opts.name}`));
program.parse();
