import { GraphQLClient, gql } from 'graphql-request';
const url = `https://graphql.contentful.com/content/v1/spaces/${process.env.CTF_SPACE}`;
const client = new GraphQLClient(url, { headers: { Authorization: `Bearer ${process.env.CTF_DELIVERY_TOKEN}` } });
const q = gql`{ __typename }`;
try { const r = await client.request(q); console.log(Object.keys(r).length>=0); } catch(e){ console.log('Provide valid tokens'); }
