import couchbase from 'couchbase';
const cluster = await couchbase.connect('couchbase://localhost', { username: 'Administrator', password: 'password' });
const bucket = cluster.bucket('default');
const coll = bucket.defaultCollection();
try { console.log(await coll.get('doc')); } catch(e){ console.log('not found'); }
await cluster.close();
