import Fastify from 'fastify';
import { z } from 'zod';
const app = Fastify();
app.post('/echo', async (req, reply)=>{ const Body=z.object({ msg:z.string() }); const parsed=Body.safeParse(req.body); if(!parsed.success) return reply.status(400).send(parsed.error.format()); return { echo: parsed.data.msg }; });
app.listen({ port: 3000 });
