files.template:
apiVersion: apps/v1
kind: Deployment
metadata: { name: app }
spec: { selector: { matchLabels: { app: app } }, template: { metadata: { labels: { app: app } }, spec: { containers: [{ name: app, image: nginx }] } } }

apiVersion: v1
kind: Service
metadata: { name: app }
spec: { selector: { app: app }, ports: [{ port: 80, targetPort: 80 }] }
