import { useForm } from 'react-hook-form';
import { create } from 'zustand';
const useStore = create<{ name: string; setName: (s:string)=>void }>((set)=>({ name: '', setName: (name)=>set({ name }) }));
export default function App(){ const { register, handleSubmit } = useForm<{ name:string }>(); const { name, setName } = useStore(); return <form onSubmit={handleSubmit(v=>setName(v.name))}><input {...register('name')} /><button>save</button><pre>{name}</pre></form>; }
