files.template:
terraform { required_version=">=1.5.0" required_providers { aws = { source="hashicorp/aws" version=">=5.0.0" } } }
provider "aws" { region = var.region }
variable "region" { type = string default = "us-east-1" }
resource "aws_lambda_function" "fn" { function_name="example" runtime="nodejs18.x" role="arn:aws:iam::123456789012:role/demo" handler="index.handler" filename="function.zip" }
