const url = 'https://api.deepgram.com/v1/listen?model=nova-2';
const res = await fetch(url, { method: 'POST', headers: { 'Authorization': `Token ${process.env.DEEPGRAM_API_KEY}`, 'Content-Type': 'application/json' }, body: JSON.stringify({ url: process.env.AUDIO_URL || '{{audio_url}}' }) });
const json = await res.json();
console.log(json.results?.channels?.[0]?.alternatives?.[0]?.transcript);
