import { S3Client, ListObjectsV2Command } from '@aws-sdk/client-s3';
const s3 = new S3Client({ endpoint: process.env.SPACES_ENDPOINT || '{{endpoint}}', forcePathStyle: false, region: 'us-east-1', credentials: { accessKeyId: process.env.SPACES_KEY || 'KEY', secretAccessKey: process.env.SPACES_SECRET || 'SECRET' } });
const res = await s3.send(new ListObjectsV2Command({ Bucket: process.env.SPACES_BUCKET || '{{bucket}}' }));
console.log(res.Contents?.length || 0);
