import { Client, GatewayIntentBits, ActionRowBuilder, ButtonBuilder, ButtonStyle, Events } from 'discord.js';
const c = new Client({ intents: [GatewayIntentBits.Guilds] });
c.on('ready', ()=>console.log('ready'));
c.on(Events.InteractionCreate, async i=>{ if(!i.isButton()) return; await i.reply('clicked'); });
// Register a message with button in your flow
c.login(process.env.BOT_TOKEN);
