import { REST, Routes, SlashCommandBuilder } from 'discord.js';
const rest = new REST({ version: '10' }).setToken(process.env.BOT_TOKEN);
await rest.put(Routes.applicationGuildCommands(process.env.APP_ID, process.env.GUILD_ID), { body: [ new SlashCommandBuilder().setName('hi').setDescription('Hello').toJSON() ] });
console.log('registered');
