const express = require('express');
const nacl = require('tweetnacl');
const app = express();
app.use(express.raw({ type: '*/*' }));
app.post('/interactions', (req,res)=>{ const sig=req.headers['x-signature-ed25519']; const ts=req.headers['x-signature-timestamp']; const isValid = nacl.sign.detached.verify(Buffer.from(ts+req.body), Buffer.from(sig, 'hex'), Buffer.from(process.env.DISCORD_PUBLIC_KEY, 'hex')); res.status(isValid?200:401).send('ok'); });
app.listen(3000, ()=>console.log('listening'));
