import { Client, GatewayIntentBits, REST, Routes, SlashCommandBuilder } from 'discord.js';
const client = new Client({ intents: [GatewayIntentBits.Guilds] });
client.once('ready', ()=>console.log('ready'));
client.on('interactionCreate', async i => { if (!i.isChatInputCommand()) return; if (i.commandName==='ping') await i.reply('pong'); });
const rest = new REST({ version: '10' }).setToken(process.env.BOT_TOKEN);
await rest.put(Routes.applicationCommands(process.env.APP_ID), { body: [ new SlashCommandBuilder().setName('ping').setDescription('Ping').toJSON() ] });
client.login(process.env.BOT_TOKEN);
