files.template:
receivers:
  otlp: { protocols: { http: {} } }
exporters:
  logging: {}
  otlphttp/loki: { endpoint: http://loki:3100/otlp }
  otlphttp/tempo: { endpoint: http://tempo:3200 }
service:
  pipelines:
    traces: { receivers: [otlp], exporters: [logging, otlphttp/tempo] }
    logs: { receivers: [otlp], exporters: [logging, otlphttp/loki] }
