// @ts-check
/** @type {import('@docusaurus/types').Config} */
module.exports = {
  title: '{{title}}',
  url: '{{url}}',
  baseUrl: '{{baseUrl}}',
  favicon: 'img/favicon.ico',
  organizationName: 'example',
  projectName: '{{app_name}}',
  i18n: { defaultLocale: 'en', locales: ['en', 'ja'] },
  themeConfig: {
    gtag: { trackingID: process.env.GA_ID || '{{ga_tracking_id}}', anonymizeIP: true },
    algolia: {
      appId: process.env.ALGOLIA_APP_ID || '{{algolia_app_id}}',
      apiKey: process.env.ALGOLIA_API_KEY || '{{algolia_api_key}}',
      indexName: process.env.ALGOLIA_INDEX_NAME || '{{algolia_index_name}}'
    }
  },
  plugins: [
    [
      '@docusaurus/plugin-pwa',
      {
        debug: false,
        offlineModeActivationStrategies: ['appInstalled', 'standalone', 'queryString'],
        pwaHead: [
          { tagName: 'link', rel: 'icon', href: '/img/logo.png' },
          { tagName: 'link', rel: 'manifest', href: '/manifest.json' },
          { tagName: 'meta', name: 'theme-color', content: '#0ea5e9' }
        ]
      }
    ]
  ],
  presets: [
    [
      '@docusaurus/preset-classic',
      /** @type {import('@docusaurus/preset-classic').Options} */({
        docs: { sidebarPath: require.resolve('./sidebars.js') },
        blog: false,
        sitemap: { changefreq: 'weekly', priority: 0.5, trailingSlash: false },
        theme: { customCss: [] }
      })
    ]
  ]
};
