files.template:
{
  "index_name": "{{algolia_index_name}}",
  "start_urls": ["{{site_url}}"],
  "sitemaps": ["{{site_url}}/sitemap.xml"],
  "selectors": {
    "lvl0": { "selector": "nav >> a.active", "type": "default" },
    "lvl1": "article h1",
    "lvl2": "article h2",
    "lvl3": "article h3",
    "text": "article p, article li"
  },
  "custom_settings": {
    "searchableAttributes": [
      "unordered(hierarchy.lvl0)",
      "unordered(hierarchy.lvl1)",
      "unordered(hierarchy.lvl2)",
      "unordered(hierarchy.lvl3)",
      "content"
    ],
    "ranking": [
      "words", "filters", "typo", "attribute", "proximity", "exact", "custom"
    ],
    "customRanking": [
      "desc(pageRank)", "desc(level)", "asc(position)"
    ]
  }
}
