import React from 'react';
export function Highlight(props: { color?: string; children: React.ReactNode }){
  const color = props.color || '#fde68a';
  return <span style={{ background: color, padding: '0 4px', borderRadius: 4 }}>{props.children}</span>;
}
export default Highlight;
