import mysql from 'mysql2/promise';
import { drizzle } from 'drizzle-orm/mysql2';
const pool = mysql.createPool('{{mysql_uri}}');
const db = drizzle(pool);
await db.execute(`CREATE TABLE IF NOT EXISTS items (id INT AUTO_INCREMENT PRIMARY KEY, name VARCHAR(255))`);
await db.execute(`INSERT INTO items(name) VALUES('hello')`);
const [rows] = await db.execute(`SELECT COUNT(*) as c FROM items`);
console.log(rows);
process.exit(0);
