import { drizzle } from 'drizzle-orm/better-sqlite3';
import Database from 'better-sqlite3';
import { sqliteTable, integer, text } from 'drizzle-orm/sqlite-core';
const sqlite = new Database('app.db');
const db = drizzle(sqlite);
const posts = sqliteTable('posts', { id: integer('id').primaryKey(), title: text('title').notNull() });
export async function list(page:number){ const limit = 10, offset = (page-1)*limit; return db.select().from(posts).limit(limit as any).offset(offset as any); }
