import pkg from 'pg';
import { drizzle } from 'drizzle-orm/node-postgres';
const { Client } = pkg;
const client = new Client({ connectionString: '{{pg_uri}}' });
await client.connect();
const db = drizzle(client);
await client.query('CREATE TABLE IF NOT EXISTS items (id serial primary key, name text)');
await client.query(`INSERT INTO items(name) VALUES('hello')`);
const res = await client.query('SELECT count(*) FROM items');
console.log('Item count', res.rows[0].count);
await client.end();
