import { drizzle } from 'drizzle-orm/better-sqlite3';
import Database from 'better-sqlite3';
import { sqliteTable, integer, text } from 'drizzle-orm/sqlite-core';
const sqlite = new Database('app.db');
const db = drizzle(sqlite);
const users = sqliteTable('users', { id: integer('id').primaryKey(), email: text('email').unique().notNull() });
export async function createUser(email: string){ try { await db.insert(users).values({ email }); return { ok:true }; } catch(e){ return { ok:false, reason:'maybe-duplicate' }; } }
