# DuckDB VSS (Concept)

> Requires DuckDB with vss extension.

-- SQL (example)
INSTALL vss; LOAD vss;
CREATE TABLE items(id INTEGER, embedding FLOAT[3]);
INSERT INTO items VALUES (1, [0.1,0.2,0.3]), (2, [0.2,0.1,0.25]);
SELECT id, vss_distance(embedding, [0.1,0.2,0.3]) AS dist FROM items ORDER BY dist LIMIT 2;
