import { Client } from '@elastic/elasticsearch';
const client = new Client({ node: process.env.ES_NODE || '{{es_node}}' });
const index = 'docs_bulk';
try { await client.indices.delete({ index }); } catch {}
await client.indices.create({ index });
const operations = [ { index: { _index: index } }, { text: 'hello' }, { index: { _index: index } }, { text: 'fluorite' } ];
await client.bulk({ operations });
await client.indices.refresh({ index });
const res = await client.search({ index, query: { match_all: {} } });
console.log(res.hits?.hits?.length || 0);
