import { Client } from '@elastic/elasticsearch';
const client = new Client({ node: process.env.ES_NODE || '{{es_node}}' });
const index = 'docs';
try { await client.indices.create({ index }); } catch {}
await client.index({ index, document: { text: 'hello', tag: 'a' } });
await client.indices.refresh({ index });
const del = await client.deleteByQuery({ index, query: { term: { tag: 'a' } } });
console.log(del.deleted || 0);
