import { Client } from '@elastic/elasticsearch';
const client = new Client({ node: process.env.ES_NODE || '{{es_node}}' });
try { const res = await client.indices.delete({ index: process.env.ES_INDEX || '{{index_name}}' }); console.log(res.body || res); } catch (e) { console.log('delete failed', e?.message); }
