import { Client } from '@elastic/elasticsearch';
const client = new Client({ node: process.env.ES_NODE || '{{es_node}}' });
const index = 'docs_knn_filter';
try { await client.indices.delete({ index }); } catch {}
await client.indices.create({ index, mappings: { properties: { text: { type: 'text' }, tag: { type: 'keyword' }, embedding: { type: 'dense_vector', dims: 3 } } } });
await client.index({ index, document: { text: 'hello', tag: 'a', embedding: [0.1,0.2,0.3] } });
await client.index({ index, document: { text: 'fluorite', tag: 'b', embedding: [0.2,0.1,0.25] } });
await client.indices.refresh({ index });
const res = await client.search({ index, knn: { field: 'embedding', query_vector: [0.1,0.2,0.3], k: 2, num_candidates: 10, filter: { term: { tag: 'a' } } }, _source: ['text','tag'] });
console.log(res.hits?.hits?.map(h => h._source?.text));
